import React, {useState} from 'react'
import InputBox from './inputBox';
import { isNumber } from '../library/functions';
import questionDatabase from '../data/data';


export default function QuestionSelections(props){
    const [inputResponse, setInputResponse]=useState("");
    const [tempNum, setTempNum]=useState(questionDatabase.map((section) => ({id: section.id, value: 0})));

    function getData(val, idIn){
        setTempNum(
            tempNum.map((num) =>
                // Here you accept a id argument to the function and replace it with hard coded ðŸ¤ª 2, to make it dynamic.
                num.id === idIn
                    ? { ...num, value: val }
                    : { ...num }
            )
        );
    }

    function handleFormSubmit(){
        console.log(tempNum);
        if (isNumber(tempNum) && tempNum >= 0 && tempNum <= questionDatabase.length)
            props.handleQuestions(tempNum);
        else
            setInputResponse("Invalid entry. Please try again.");
    }

    return(
        <>
            {questionDatabase.map((section) => (
                <div className='section-question-number-prompt'>
                    <InputBox 
                        label = {section.sectionTitle}
                        numQuestions = {section.sectionContent.length}
                        sectionContent = {section.sectionContent}
                        onChange = {getData}
                        id = {section.id}
                    />
                    <br/>
                </div>
            ))}
    
            <button onClick={()=>handleFormSubmit()}>Submit</button>
            {inputResponse}
        </>
    )
}
