import React from 'react'
import questionDatabase from './data'

var numValue = -1;
export default function UserChoice(){
    var inputResponse = "";

    function isNumber(str){
        if (str.trim() === ''){
            return false;
        }

        return !isNaN(str);
    }

    const handleFormSubmit = event =>{
        
        if (isNumber(event.target.value) && event.target.value >= 0){
            numValue = event.target.value;
        }
        else{
            inputResponse = "Invalid entry. Please try again.";
            console.log("No");
        }


    }

    return (
        <div className='question-number-input'>
            Enter a number from 1-{questionDatabase.length} for the number of questions in your quiz.<br/>
            <div className='question-input-field'>
                <form>
                    <label>
                        Question Amount: 
                        <input 
                            type='text' 
                            value={numValue}
                            onChange={handleFormSubmit}
                        />
                    </label>
                    <input type='submit' value='submit'/>
                    {inputResponse}
                </form>
            </div>
        </div>
    )
}

export {numValue};
