import React, {useState} from 'react'
import questionDatabase from './data';

export default function App(){
    const [inputResponse, setInputResponse]=useState("");
    const [tempNum, setTempNum]=useState(-1);
    const [numValue, setNumValue]=useState(-1);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [currentScore, setCurrentScore] = useState(0);
    const [quizCompleted, setQuizCompleted] = useState(false);
    const [randomQuestionList, setRandomQuestionList] = useState([]);
    

    function handleAnswerOptionClick(isCorrect){
        if (isCorrect)
            setCurrentScore(currentScore + 1);

        var nextQuestion = currentQuestion + 1;
        if (nextQuestion == numValue)
            setQuizCompleted(true);
        
        setCurrentQuestion(nextQuestion);
    }

    function shuffleAnswers(){
        var answers = []
        for (let i = 0; i < questionDatabase[randomQuestionList[currentQuestion]-1].incorrectAnswers.length; i++)
            answers.push(questionDatabase[randomQuestionList[currentQuestion]-1].incorrectAnswers[i]);
        
        answers.push(questionDatabase[randomQuestionList[currentQuestion]-1].correctAnswer);

        let currentIndex = answers.length,  randomIndex;
        
        while (currentIndex != 0) {
            randomIndex = Math.floor(Math.random() * currentIndex);
            currentIndex--;
        
            [answers[currentIndex], answers[randomIndex]] = [
            answers[randomIndex], answers[currentIndex]];
        }
        return answers;
    }

    function isNumber(str){
        if (str.trim() === ''){
            return false;
        }
        return !isNaN(str);
    }

    function getData(val){
        setTempNum(val.target.value);
    }

    function handleFormSubmit(){
        if (isNumber(tempNum) && tempNum >= 0 && tempNum <= questionDatabase.length){
            RandomizeQuestions();
            setNumValue(tempNum);
        }
        else{
            setInputResponse("Invalid entry. Please try again.");
        }
    }

    function RandomizeQuestions(){
        var fullList = [];
        for (let i = 0; i < questionDatabase.length; i++){
            console.log(i);
            fullList.push(i+1);
        }

        let currentIndex = fullList.length,  randomIndex;
        while (currentIndex != 0) {
            randomIndex = Math.floor(Math.random() * currentIndex);
            currentIndex--;
        
            [fullList[currentIndex], fullList[randomIndex]] = [
            fullList[randomIndex], fullList[currentIndex]];
        }
        console.log(fullList);
        setRandomQuestionList(fullList);
        return;
    }

    return(
        <div className='quiz-app'>
            <h1>Test Quiz</h1>
            {(numValue === -1) ? (
                <div className='question-number-input'>
                Enter a number from 1-{questionDatabase.length} for the number of questions in your quiz.<br/>
                    <div className='question-input-field'>
                        <label>
                            Question Amount: 
                            <input 
                                type='text' 
                                onChange={getData}
                            />
                        </label>
                        <button onClick={()=>handleFormSubmit()}>Submit</button>
                        {inputResponse}
                    </div>
                </div>
            ) : (
                <div className='quiz-screen'>
                    <div className='q&a-secion'>
                        {quizCompleted ? (
                            <div className='final-score'> Final Score: {currentScore}/{currentQuestion} </div>
                        ) : (
                            <>
                            <div id="prompt">Choose the correct answer out of the four given answers.</div>
                            <br/>
                                <div className='question-section'>
                                    <div className='question-count'> Question {currentQuestion+1} / {numValue} </div>
                                    <div className='question-text'> {questionDatabase[randomQuestionList[currentQuestion]-1].question} </div>
                                </div>

                                <div className='answer-section'>
                                    {shuffleAnswers().map((answerOption) => (
                                        <button onClick={() => handleAnswerOptionClick(answerOption === questionDatabase[randomQuestionList[currentQuestion]-1].correctAnswer)}>
                                            {answerOption}
                                        </button>
                                    ))} 
                                </div>
                                <div className='score-section'>Current Score: {currentScore}/{currentQuestion}</div>
                            </>
                        )}
                    </div>
                </div>
            )}
        </div>
    )
}
