import React, {useState} from 'react'
import questionDatabase from './data'

export default function Quiz(){
    
    const [currentQuestion, setCurrentQuestion] = useState(0);
    var ANSWER_COUNT = 4;
    
    function randNum(){
        return (Math.floor(Math.random() * 4));
    }
    
    function handleAnswerOptionClick(isCorrect){
        if (isCorrect){

        }

    }

    function shuffleAnswers()
    {
        var answers = []
        for (let i = 0; i < questionDatabase.length; i++)
            answers.push(questionDatabase[currentQuestion].incorrectAnswers[i]);
        
        answers.push(questionDatabase[currentQuestion].correctAnswer);

        let currentIndex = answers.length,  randomIndex;
        
        // While there remain elements to shuffle.
        while (currentIndex != 0) {
        
            // Pick a remaining element.
            randomIndex = Math.floor(Math.random() * currentIndex);
            currentIndex--;
        
            // And swap it with the current element.
            [answers[currentIndex], answers[randomIndex]] = [
            answers[randomIndex], answers[currentIndex]];
        }

        return answers;
    }


    return(
        <div className='quiz-screen'>
            <div className='question-section'>
                <div className='question-count'> Question {currentQuestion+1} / {questionDatabase.length} </div>
                <br></br>
                <div className='question-text'> {questionDatabase[currentQuestion].question} </div>
            </div>

            <div className='answer-section'>
                {shuffleAnswers().map((answerOption) => (
                    <button onClick={() => handleAnswerOptionClick(answerOption === questionDatabase[currentQuestion].correctAnswer)}>
                        {answerOption}
                    </button>
                ))} 
            </div>

            <div className='score-section'>

            </div>

        </div>
    )
}
