import React, {useState} from 'react'
import questionDatabase from './data'
var numValue = -1;
export {numValue};

export default function UserChoice(){
    const [inputResponse, setInputResponse]=useState("");
    const [tempNum, setTempNum]=useState(-1);

    function isNumber(str){
        if (str.trim() === ''){
            return false;
        }
        return !isNaN(str);
    }

    function getData(val){
        setTempNum(val.target.value);
    }

    function handleFormSubmit(){
        if (isNumber(tempNum) && tempNum >= 0 && tempNum <= questionDatabase.length){
            numValue = tempNum;
        }
        else{
            setInputResponse("Invalid entry. Please try again.");
        }
    }

    return (
        <div className='question-number-input'>
            Enter a number from 1-{questionDatabase.length} for the number of questions in your quiz.<br/>
            <div className='question-input-field'>
                <label>
                    Question Amount: 
                    <input 
                        type='text' 
                        onChange={getData}
                    />
                </label>
                <button onClick={()=>handleFormSubmit()}>Submit</button>
                {inputResponse}
            </div>
        </div>
    )
}
